create table posts (
	post_id INT PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	post_title VARCHAR(100),
	Post_content TEXT,
	media_file VARCHAR(50),
	category_id INT REFERENCES categories(category_id) ON DELETE CASCADE,
	post_by INT REFERENCES users_profile(user_profile_id) ON DELETE CASCADE,
	created_at DATE,
	updated_at DATE,
	answer_by INT REFERENCES users_profile(user_profile_id) ON DELETE CASCADE,
	comment_id INT REFERENCES comments(comment_id) ON DELETE CASCADE,
	share INT,
	post_vote INT
);
